import React from 'react';
import { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

const Challenge5 = () =>{
    const [uname , setUname] = useState('');
    const [fullname , setFullname] = useState('');
    const [age , setAge] = useState();

    const displaydata =() =>{
        return <h1> {uname} </h1>
    }

    return(
        <>
            <form className='d-flex flex-column'>
                <div>
                <div style={{fontWeight:"bolder"}}>UserName :</div> 
                <input className='mb-3' type='text' value={uname} onChange={(e) => setUname(e.target.value)} />
                </div>
                <div>
                <div style={{fontWeight:"bolder"}}>FullName :</div> 
                <input className='mb-3' type='text' value={fullname} onChange={(e) => setFullname(e.target.value)} />
                </div>
                <div>
                <div style={{fontWeight:"bolder"}}>Age :</div> 
                <input className='mb-3'  type='number' value={age} onChange={(e) => setAge(e.target.value)} />
                </div>
                <div>
                <button> SUBMIT </button>
                </div>
            </form>
        </>
    )
}

 export default Challenge5;
