import React, { useState } from 'react';
import { BrowserRouter, Routes, Route , useNavigate } from "react-router-dom";

exports.Results = () =>{ return <> <h1> THANK YOU FOR SUBMITTING DATA </h1></>}
exports.Challenge7 = () =>{
    const [name ,setName] = useState('');
    const [age , setAge] = useState('');

    const Navigate = useNavigate();


    const submitdata = () =>{
        Navigate("/results");
    }

    // const handleinp = (e) =>{
    //     setName(e.target.value) = e.target.value;
    // }

    return(
        <>
            <form onSubmit={submitdata}>
                <div>Name : <input type='text' value={name} onChange={(e)=>(setName(e.target.value))}/></div>
                <div>Age : <input  type='number' value={age} onChange={(e)=>(setAge(e.target.value))}/></div>

                <input type='submit' />
            </form>
        </>
    )
}

// export default {Challenge7,Results};
// export default Challenge7;

module.exports = {}
