import React from 'react';
import ReactDOM from 'react-dom/client';
import { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Blogs from "./challenge";

export default function Application() {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Hrb />}>
        <Route path="blogs" element={<Blogs/>} />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}




function Hrb() {
  function Hello() {
    let mystyle = {
      height:"50px",
      width:"100px",
      backgroundColor:"purple",
      padding:"10px",
      marginTop:"20px"
    }
    const [show, setShow] = useState(true);

    return (
      <div className="container">
        <button style={mystyle} onClick={() => setShow((show) => !show)}> Show / Hide</button>
        {show ? <h1>Welcome to React Challenges</h1> : null}
      </div>
    );
  }

  const container = document.getElementById('root12');
  const root12 = ReactDOM.createRoot(container);
  root12.render(<Hello />);

}
