    age: ""
  };

const Challenge5 = () =>{
    const [obj , setObj] = useState(initialState);
    const [display, setDisplay] = useState(false);

    const printValues = (e) => {
        e.preventDefault();
        setDisplay(true);
      };
      const updateField = (e) => {
        setObj({
          ...obj,
          [e.target.name]: e.target.value
