import { StrictMode } from "react";
import React from 'react';
import ReactDOM from 'react-dom/client';
import { useState,useEffect } from "react";


function challenge1() {
  function Hello() {
    let mystyle = {
      height:"50px",
      width:"100px",
      backgroundColor:"purple",
      padding:"10px",
      marginTop:"20px"
    }
    const [show, setShow] = useState(true);

    return (
      <div className="container">
        <button style={mystyle} onClick={() => setShow((show) => !show)}> Show / Hide</button>
        {show ? <h1>Welcome to React Challenges</h1> : null}
      </div>
    );
  }
  const container = document.getElementById('root');
  const root = ReactDOM.createRoot(container);
  root.render(<Hello />);
}
function challenge2() {
  function Timer() {
    const [count,setCount] = useState(0);
    const [intervalId , SetintervalId] = useState(null);
    let start = {
      padding:"25px",
      backgroundColor:"green",
      textAlign:"center"
    }
    let stop = {
      padding:"25px",
      backgroundColor:"red",
      textAlign:"center"
    }
    let reset = {
      padding:"25px",
      backgroundColor:"yellow",
      textAlign:"center"
    }
    useEffect(()=>{
      return()=>{
        if (intervalId) {
            clearInterval(intervalId);
        }
      };
   },[intervalId]);
    return(
      <>
      <div style={{textAlign: "center",marginTop: "400px",fontSize:"30px"}}>{count} seconds</div>
      <div style={{textAlign:"center"}}>
        <button style={start} onClick={()=>{}
        }> Start </button>
        <button style={stop} onClick={()=>{}}> Stop </button>
        <button style={reset}> Reset </button>
      </div>
      </>
    )
  }
  const container = document.getElementById('root');
  const root = ReactDOM.createRoot(container);
  root.render(<Timer />);
}
challenge2();
