import React from 'react';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import ReactDOM from 'react-dom/client';
import { useState, useEffect } from "react";
import './challanges/challenge1';
import './challanges/challenge2';
import './challanges/challenge3';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';



function challenge3() {
  function ToDo() {
    const [todoList, setTodoList] = useState([]);
    const [input, setInput] = useState("");

    const addItem = () => {
      if (input.length) {
        const item = { id: Math.random(), todo: input, isCompleted: false };
        setTodoList([...todoList, item]);
        setInput("");
      }
    };
    return (
      <div className={"m-auto pt-5"} style={{ width: "500px" }}>
        <h2>Todo List</h2>
        <div>
          <input
            placeholder="Enter City"
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button onClick={addItem}>Add</button>
        </div>

        <div>
          <ul>
            {todoList.map(({ id, todo }) => (
              <li>
                {todo}
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
  const container = document.getElementById('root');
  const root = ReactDOM.createRoot(container);
  root.render(<ToDo />);
}
function challenge4() {
  function Progress() {
    const [input, setInput] = useState(10);
    return (
      <>
        <div style={{ fontFamily: 'Arial', width: "500px" }}>
          <h1 className="text-center">Progress Bar</h1>

          <div>
            <div className="container">
              {input >= 0 && input <= 100 ? (
                <div className="innerContainer" style={{ width: `${input}%` }}>
                  {input}%
                </div>
              ) : (
                alert("please enter value less than 100")
              )}
            </div>
          </div>
          <div>Input : <input type="number" value={input} onChange={(e) => setInput(e.target.value)} /></div>
        </div>
      </>
    )
  }

  const container = document.getElementById('root');
  const root = ReactDOM.createRoot(container);
  root.render(<Progress />);
}
challenge4();
