import React, { useState } from 'react';
import { BrowserRouter, Routes, Route , useNavigate } from "react-router-dom";

const Challenge7 = () =>{
    const [name ,setName] = useState('');
    const [age , setAge] = useState('');

    const Navigate = useNavigate();

    const Results = () =>{ return <> <h1> THANK YOU FOR SUBMITTING DATA </h1></>}

    const submitdata = () =>{
        Navigate("/results");
    }

    // const handleinp = (e) =>{
    //     setName(e.target.value) = e.target.value;
    // }

    return(
        <>
            <form onSubmit={submitdata}>
                <div>Name : <input type='text' value={name} onChange={(e)=>(setName(e.target.value))}/></div>
                <div>Age : <input  type='number' value={age} onChange={(e)=>(setAge(e.target.value))}/></div>

                <input type='submit' />
            </form>

            <BrowserRouter>
                <Routes>
                    <Route path='/Challenge7' element={<Challenge7 />} />
                    <Route path='/Challenge7/results' element={<Results />} />
                </Routes>
            </BrowserRouter>
        </>
    )
}

export default Challenge7;
