import React from 'react';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import ReactDOM from 'react-dom/client';
import { useState, useEffect } from "react";
import challenge1 from  './challanges/challenge1';
import challenge2 from  './challanges/challenge2';
import challenge3 from  './challanges/challenge3';
import home from  './challanges/home';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';



function challenge4() {
  function Progress() {
    const [input, setInput] = useState(10);
    return (
      <>
        <div style={{ fontFamily: 'Arial', width: "500px" }}>
          <h1 className="text-center">Progress Bar</h1>

          <div>
            <div className="container">
              {input >= 0 && input <= 100 ? (
                <div className="innerContainer" style={{ width: `${input}%` }}>
                  {input}%
                </div>
              ) : (
                alert("please enter value less than 100")
              )}
            </div>
          </div>
          <div>Input : <input type="number" value={input} onChange={(e) => setInput(e.target.value)} /></div>
        </div>
      </>
    )
  }

  const container = document.getElementById('root');
  const root = ReactDOM.createRoot(container);
  root.render(<Progress />);
}

export default function Application() {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<home />}>
          <Route index element={<Home />} />
          <Route path="blogs" element={<Blogs />} />
          <Route path="contact" element={<Contact />} />
          <Route path="*" element={<NoPage />} />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}
